INSERT INTO product (product_id, name, description, price, quant_in_stock, product_condition, condition_details, date_created, pct_discount, views, category, seller_id) VALUES
(1, 'Vintage Leather Messenger Bag - Distressed Brown', 'A classic, genuine leather messenger bag with a rich, distressed brown finish. Features multiple compartments, adjustable shoulder strap, and secure buckle closures. Perfect for everyday use or travel. Shows minor signs of wear consistent with age, adding to its vintage charm.', 450.00, 5, 'Used', 'Minor scuffs and scratches on the leather surface.', DATE_SUB(CURDATE(), INTERVAL FLOOR(RAND() * 730) DAY), 0, 125, 'Clothing', 2),
(2, 'Ergonomic Office Chair with Lumbar Support', 'A high-back ergonomic office chair designed for maximum comfort and support during long hours of work. Features adjustable lumbar support, armrests, seat height, and tilt mechanism. Breathable mesh back promotes airflow. Brand new, unassembled in original packaging.', 1299.99, 20, 'New', NULL, DATE_SUB(CURDATE(), INTERVAL FLOOR(RAND() * 730) DAY), 0, 310, 'Home & Garden', 4),
(3, 'Set of Four Hand-Painted Ceramic Mugs - Floral Design', 'A beautiful set of four unique ceramic mugs, each hand-painted with a delicate floral design in vibrant colors. Perfect for enjoying your morning coffee or tea. Dishwasher and microwave safe. Brand new, in a gift box.', 280.00, 35, 'New', NULL, DATE_SUB(CURDATE(), INTERVAL FLOOR(RAND() * 730) DAY), 0, 85, 'Home & Garden', 6),
(4, 'Refurbished Apple iPhone 12 - 128GB - Black', 'A fully functional Apple iPhone 12 with 128GB of storage in black. Professionally refurbished, tested, and cleaned. May have minor cosmetic imperfections such as light scratches. Comes with a charging cable and adapter in a generic box. Backed by a 90-day warranty.', 8500.00, 12, 'Refurbished', 'Minor scratches on the screen and casing', DATE_SUB(CURDATE(), INTERVAL FLOOR(RAND() * 730) DAY), 5, 540, 'Electronics', 9),
(5, 'Outdoor Camping Tent - Sleeps 4 - Waterproof', 'A spacious and durable four-person camping tent, designed for all-weather conditions. Features a waterproof outer layer, sturdy fiberglass poles, and excellent ventilation. Easy to set up and pack down. Used once for a weekend trip', 950.00, 8, 'Used', 'Slight dirt marks on the bottom, otherwise in perfect working order.', DATE_SUB(CURDATE(), INTERVAL FLOOR(RAND() * 730) DAY), 0, 180, 'Home & Garden', 12),
(6, 'Brand New Wireless Bluetooth Headphones - Noise Cancelling', 'High-quality wireless Bluetooth headphones with active noise-cancelling technology for immersive sound. Features comfortable over-ear cushions, long battery life, and a built-in microphone for calls. Brand new in sealed packaging.', 1599.00, 25, 'New', NULL, DATE_SUB(CURDATE(), INTERVAL FLOOR(RAND() * 730) DAY), 0, 420, 'Electronics', 14),
(7, 'Antique Wooden Wall Clock - Pendulum Style', 'An elegant antique wooden wall clock with a classic pendulum design. Features Roman numeral markings and ornate detailing. The clock mechanism has been recently serviced and is in working order. Shows signs of age and wear, adding to its antique charm.', 780.00, 3, 'Used', 'Some fading of the wood finish, minor scratches on the clock face cover.', DATE_SUB(CURDATE(), INTERVAL FLOOR(RAND() * 730) DAY), 10, 65, 'Home & Garden', 17),
(8, 'Gaming Keyboard - Mechanical with RGB Backlighting', 'A high-performance mechanical gaming keyboard with customizable RGB backlighting. Features tactile and clicky key switches for a responsive typing experience. Includes anti-ghosting and N-key rollover. Brand new in original box.', 1150.00, 18, 'New', NULL, DATE_SUB(CURDATE(), INTERVAL FLOOR(RAND() * 730) DAY), 5, 290, 'Electronics', 23),
(9, 'Second-Hand Ladies Leather Boots - Size 7', 'A stylish pair of ladies'' leather boots in size 7. Features a comfortable heel height and a durable sole. Worn a few times, in very good condition. Cleaned and ready to wear.', 550.00, 7, 'Used', 'Minor creasing on the leather.', DATE_SUB(CURDATE(), INTERVAL FLOOR(RAND() * 730) DAY), 0, 110, 'Clothing', 27),
(10, 'Digital Kitchen Scale - Stainless Steel - High Precision', 'A sleek and accurate digital kitchen scale with a stainless steel platform. Features a large LCD display and multiple weighing units. Brand new in original packaging.', 220.00, 40, 'New', NULL, DATE_SUB(CURDATE(), INTERVAL FLOOR(RAND() * 730) DAY), 0, 70, 'Home & Garden', 2),
